INSERT INTO `resume` (`id`, `titre`, `description`, `limites`, `date_ad`) VALUES
(8, 'أيت كــــرمون في كلمات', 'قريَة تتـواجد بالمنطقة الجنوبية بالمغرب و بجهة كلميم واد نون، وبالتحديد بالجنوب الشرقي لمدينة تيـِزنـيـت ، بقبيلة \'إمجاض.\r\nتبعد أيت كرمون عن تيزنـيت بما يقارب ٦٠ كلم، وتتبع إذاريا لجماعة سبت النابور منذ أوخر القرن العشرين، حيث كانت تابعثا لجماعة تغيرت التي عرفت إنشقاقا في تلك الفترة.\r\nمنذ الإعلان، سنة ٢٠٠٩، عن تعيين سيدي إفني كعمالة جذيدة بجنوب البلاد، تم إعادة التقسيم الإداري و إنضمام إمجاض عامة و أيت كرمون خاصة إلى هذه العمالة بدلا من تيزنيت رغم تقرب هذه الأخيرة من الساكنة', '[\"[[\\\"29.404203\\\",\\\"-9.564946\\\"],[\\\"29.424477\\\",\\\"-9.557974\\\"],[\\\"29.425681\\\",\\\"-9.551234\\\"],[\\\"29.426611\\\",\\\"-9.546258\\\"],[\\\"29.431936\\\",\\\"-9.527980\\\"],[\\\"29.423826\\\",\\\"-9.527339\\\"],[\\\"29.422199\\\",\\\"-9.527179\\\"],[\\\"29.413000\\\",\\\"-9.527479\\\"],[\\\"29.405300\\\",\\\"-9.527179\\\"],[\\\"29.389500\\\",\\\"-9.528567\\\"],[\\\"29.381530\\\",\\\"-9.531279\\\"],[\\\"29.376330\\\",\\\"-9.532079\\\"],[\\\"29.368630\\\",\\\"-9.533879\\\"],[\\\"29.366000\\\",\\\"-9.535900\\\"],[\\\"29.363525\\\",\\\"-9.541070\\\"],[\\\"29.361750\\\",\\\"-9.547300\\\"],[\\\"29.361550\\\",\\\"-9.555196\\\"],[\\\"29.370051\\\",\\\"-9.555217\\\"],[\\\"29.378424\\\",\\\"-9.559285\\\"]]\"]', '2020-04-15 23:58:56');

